      showFollowupDialog,
      followupAnalysis,
      isLoadingFollowup,
      selectFollowupOption,
      payForFollowup,
      getFollowupAnalysis,
      checkPaidFollowups,
      getBaziResult,
      ref,
      onMounted,
      reactive,
      computed,
      get useRoute() {
        return useRoute;
      },
      get useRouter() {
        return useRouter;
      },
      get Toast() {
        return Toast;
      },
      get Dialog() {
        return Dialog;
      },
      get jsPDF() {
        return jsPDF;
      },
      get html2canvas() {
        return html2canvas;
      },
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
