        } else {
          console.warn('获取追问列表失败或返回空列表');
        }
      } catch (error) {
        console.error('加载追问分析结果出错:', error);
        // 出错时不显示错误提示，静默失败
      }
    };

    // 解析八字数据
    const parseBaZiData = data => {
      if (!data) return null;
      return {
        yearPillar: data.yearPillar || {
          heavenlyStem: '未知',
          earthlyBranch: '未知'
        },
        monthPillar: data.monthPillar || {
          heavenlyStem: '未知',
          earthlyBranch: '未知'
        },
        dayPillar: data.dayPillar || {
          heavenlyStem: '未知',
          earthlyBranch: '未知'
        },
        hourPillar: data.hourPillar || {
          heavenlyStem: '未知',
          earthlyBranch: '未知'
        },
        shenSha: data.shenSha || {
          dayChong: '未知',
          zhiShen: '未知',
          pengZuGan: '未知',
          pengZuZhi: '未知',
          xiShen: '未知',
          fuShen: '未知',
          caiShen: '未知',
          benMing: [],
          yearGan: [],
          yearZhi: [],
          dayGan: [],
          dayZhi: []
        },
        daYun: data.daYun || {
          startAge: 0,
          startYear: 0,
          isForward: true,
          daYunList: []
        },
        flowingYears: data.flowingYears || []
      };
    };

    // 添加分析状态检测函数
    const isAnalysisItemLoading = key => {
      if (!aiAnalysis.value) return true;
      const value = aiAnalysis.value[key];
      return !value || value === '暂无' || value.includes && value.includes('正在分析');
    };

    // 清理组件时移除定时器
    onUnmounted(() => {
      if (analyzeTimer.value) {
        clearInterval(analyzeTimer.value);
        analyzeTimer.value = null;
      }
    });

    // 刷新特定追问的分析结果
    const reloadFollowupAnalysis = async area => {
      if (!area) return;
      try {
        isLoadingFollowup.value = true;
        Toast.loading({
          message: '正在刷新分析结果...',
          duration: 0
        });
        console.log(`刷新${area}分析结果`);
        const analysis = await getFollowupAnalysis(area);
        if (analysis && typeof analysis === 'string' && analysis.length > 0) {
          Toast.success('刷新成功');
          console.log(`成功刷新${area}分析结果，长度: ${analysis.length}`);
        } else {
          Toast.fail('分析结果尚未生成完成');
          console.warn(`刷新${area}分析结果失败或为空`);
        }
      } catch (error) {
        console.error(`刷新${area}分析失败:`, error);
        Toast.fail('刷新失败，请重试');
      } finally {
        isLoadingFollowup.value = false;
        Toast.clear();
      }
    };
    const __returned__ = {
      route,
      router,
      resultId,
      activeTab,
      loading,
      isAnalyzing,
      analyzeProgress,
      analyzeTimer,
      userAge,
      getAnalysisContent,
      focusAreas,
      analysisResult,
      baziData,
      aiAnalysis,
      testApiConnection,
      getElementName,
      onClickLeft,
      downloadPDFAsStream,
      downloadPDF,
      generatePDFLocally,
      shareResult,
      handleLocalPDFGeneration,
      checkAnalysisStatus,
      pollAnalysisStatus,
      reloadBaziData,
      followupOptions,
      currentFollowup,
      showFollowupDialog,
      followupAnalysis,
      isLoadingFollowup,
      selectFollowupOption,
      payForFollowup,
      getFollowupAnalysis,
      checkPaidFollowups,
      getBaziResult,
      initFollowupOptions,
      loadFollowupResults,
      parseBaZiData,
      isAnalysisItemLoading,
      reloadFollowupAnalysis,
      ref,
      onMounted,
      reactive,
      computed,
      watch,
      onUnmounted,
      get useRoute() {
        return useRoute;
      },
      get useRouter() {
        return useRouter;
      },
      get Toast() {
        return Toast;
      },
      get Dialog() {
        return Dialog;
      },
      get jsPDF() {
        return jsPDF;
      },
      get html2canvas() {
        return html2canvas;
      },
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
